import "./post.css"
import {MoreVert} from "@material-ui/icons"
import { useEffect, useState } from "react";
import axios from "axios"
export default function Post({post}) {
  const [like, setLike] = useState(post.like);
  const [isLiked, setIsLiked] = useState(false);
  const [user, setUser] = useState({});
  const PF = process.env.REACT_APP_PUBLIC_FOLDER;

  useEffect(()=>{
    const fetchUser = async() => {
      const res = await axios.get("posts/feed/61b63091c62b86e73a162c32");
      setPosts(res.data)
    }
    fetchPosts();
  },[])

  const likeHandler =()=>{
    setLike(isLiked?like-1:like+1)
    setIsLiked(!isLiked)
  }

  return (
    <div className="post">
      <div className="postWrapper">
        <div className="postTop">
          <div className="postTopLeft">
            <img className="postDP" src={Users.filter(u=>u.id===post.userId)[0].profilePicture} alt="" />
            <span className="postUser">
              {Users.filter(u=>u.id===post.userId)[0].username}
            </span>
            <span className="postDate">{post.date}</span>
          </div>
          <div className="postTopRight">
            <MoreVert/>
          </div>
        </div>
        <div className="postCenter">
          <span className="postContent">{post?.desc}</span>
          <img className="postImage" src={PF+post.photo} alt="" />
        </div>
        <div className="postBottom">
          <div className="postBottomLeft">
            <img className="likeIcon" src={`${PF}like.png`} onClick={likeHandler} alt="" />
            <span className="likeCount">{like} likes</span>
          </div>
          <div className="postBottomRight">
            <span className="commentCount">{post.comment} comment</span>
          </div>
        </div>
      </div>
    </div>
  )
}
